$(document).ready(function() {
  $('head').append('<link rel="stylesheet" href="https://res.cloudinary.com/thiagocanudo/raw/upload/v1532642785/getchat/css/getchat.min.css" type="text/css"/>');
    var $btnChat = $("#btnGetChat");
    var $chatBody = $("#getChatBody");
    $(document).on("click", function(e) {
      $chatBody.slideUp();
      $btnChat.removeClass("closeGetChat");
    });
    $chatBody.on("click", function(e) {
      e.stopPropagation();
    });
    $btnChat.on("click", function(e) {
      $(this).toggleClass("closeGetChat");
      $chatBody.stop().slideToggle();
      e.stopPropagation();
    });
});  